import React, { Component } from 'react';
import { Container, Col, Row, Card, Button } from 'react-bootstrap';
export default class ProductDetail extends Component {
  state = {
    data: this.props.data,
  }




  render() {
    let  prodect = this.state.data.find((element) => {
      const { match: { params } } = this.props;

      return element.id == params.id

  })


    return (
      <div>

        <Container>
          <Row>
            <Col sm={6} elementType="card" >
              <Card.Img className="productDetailImage" variant="top" src={prodect.image} />
            </Col>
            <Col sm={4}>
              <Card.Body>
                <br></br>



    <Card.Title className="description">{prodect.name}</Card.Title>
                <br></br>
                <Card.Text className="description">
               {prodect.desc}
    </Card.Text>

                <style type="text/css">
                  {`
    `}
                </style>
                <Button variant="secondary" onClick ={() => { localStorage.setItem('items' , [prodect.id,prodect.name , prodect.price]) } }>Add to Cart</Button>
              </Card.Body>
            </Col>
          </Row>
        </Container>
      </div>
    )
  }
}
