import React, { Component } from "react";
import { Table, Button ,Card} from "react-bootstrap";
export default class Cart extends Component {
  state = {
    cart: this.props.cart
  };
  componentDidMount() {

    var cart = JSON.parse(localStorage.getItem("product_cart"));
    let temp = this.state.cart;

    // temp.push(this.state.cart)
    temp.push(cart);
    this.setState({ cart: temp });

  }
  render() {
    const x = this.state.cart

    const sum = x.map((e,i) => {     return <Card style={{ width: "20rem" }}>
       <Card.Body>
       <Card.Title>{e[i].name}</Card.Title>
         <Card.Text>
         {e[i].desc}
         </Card.Text>
         <Card.Title>{e[i].price}</Card.Title>
       </Card.Body>
     </Card>})




    console.log(sum)


    return (
      <div>
{sum}
      </div>
    );
  }
}
