import React, { Component } from 'react'
import { Button, Form  } from 'semantic-ui-react'
import { add }  from '../functionAuth/functionAuth'

export default class Owneradd extends Component {
    state = {
    };
    onChangHandler = e => {
      this.setState({
        [e.target.name]: e.target.value
      });
    };
    onSubmitHandelr = e => {
      e.preventDefault();
      add(this.state);
      this.props.history.push("/Home");
    };
    onChange = data => this.setState({ data })
    render() {

      return (
        <div >
         <h3 className="ttb">Add a Prodect</h3>

          <Form onSubmit={this.onSubmitHandelr}>
            <Form.Field className="form-group">
              <label className="tb">Item name</label>
              <input
                placeholder="Item Name"
                name="item_name"
                onChange={this.onChangHandler}
              />
            </Form.Field>
            <Form.Field className="form-group">
              <label className="tb">Item Price</label>
              <input
                placeholder="Item Price"
                name="item_price"
                onChange={this.onChangHandler}
              />
            </Form.Field>
            <Form.Field className="form-group">
              <label className="tb">item_desc</label>
              <input
                placeholder="item Desc"
                name="item_price"
                onChange={this.onChangHandler}
              />
            </Form.Field>
            <Form.Field className="form-group">
              <label className="tb">item_image</label>
              <input
                placeholder="item Image"
                name="item_image"
                onChange={this.onChangHandler}
              />
            </Form.Field>

            <Button type="submit" className="btn btn-primary btn-block">
              Submit
            </Button>

          </Form>
        </div>
      );
    }
  }
