import React from 'react'
import { Map, GoogleApiWrapper, Marker } from 'google-maps-react';
import Axios from 'axios';
export class MapContainer extends React.Component {
  state = { userLocation: { lat: 32, lng: 32 }, loading: true,data:null };

  componentDidMount(props) {
    fetch('http://localhost:2551/api/v1/stores/')
    .then((response) => response.json())
      .then((responseData) => {
        // console.log(responseData2);
        this.setState({
          data: responseData,

        })
      }).then( navigator.geolocation.getCurrentPosition(
        position => {
          const { latitude, longitude } = position.coords;
          this.setState({
            stores: [{ lat: this.state.data.store_coordinates.lat, lng:this.state.data.store_coordinates.long},
           ],
            userLocation: { lat: latitude, lng: longitude },
            loading: false
          });
        },
        () => {
          this.setState({ loading: false });
        }
      ))

  }
  displayMarkers = () => {
    return this.state.data.store_coordinatesmap((store, index) => {
      return <Marker key={index} id={index} position={{
        lat: store.latitude,
        lng: store.longitude
      }}
        onClick={() => console.log("You clicked me!")} />
    })
  }
  render() {
        console.log(this.state.data)

    const { loading, userLocation } = this.state;
    const { google } = this.props;
    if (loading) {
      return null;
    }
    return (
      <Map google={google} initialCenter={userLocation} zoom={15}>
        <Marker onClick={() => console.log("You clicked me!")} />
        {this.displayMarkers()}
      </Map>
    );
  }
}
export default GoogleApiWrapper({
  apiKey: process.env.REACT_APP_API
})(MapContainer);
