import React, { Component } from "react";
import { Table, Button } from "react-bootstrap";
import Dialog from 'react-bootstrap-dialog'
export default class Cart extends Component {
    constructor(){
        super()
        this.onClick = this.onClick.bind(this)
    }

onClick () {
    Dialog.TextPrompt()
  }

  render() {
    let elm =''
    let sum=0
  var cart = JSON.parse(localStorage.getItem("product_cart"));
  if (cart != null) {
    let group = cart.reduce((r, a) => {
      //   console.log("a", a);
      //   console.log("r", r);
      r[a.id] = [...(r[a.id] || []), a];
      return r;
    }, {});
    let cartelm = [];
    let j = 0;
    let total = [];
    for (let i in group) {
      cartelm.push(
        group[i].map(elm => {
          // console.log(elm);
          // console.log(elm.price)
          let price = elm.price * group[i].length;
          total.push(price);
          // console.log(group[i].length)
          if (group[i].indexOf(elm) == 0) {
            j++;
            return (
              <tr key={elm._id}>
                <td>{j}</td>
                <td>{elm.item_name}</td>
                <td>{group[i].length}</td>
                <td>{price}</td>
              </tr>
            );
          }
        })
      );
    }
    // console.log(total)
    const reducer = (accumulator, currentValue) => accumulator + currentValue;
    sum = total.reduce(reducer);
    elm = cartelm.map(elm => {
      return elm;
    });
  }

    return (
      <div>
        <Table striped bordered hover>
          <thead>
            <tr>
              <th>#</th>
              <th>Item</th>
              <th>Quantity</th>
              <th>Price</th>
            </tr>
          </thead>
          <tbody>
            {elm}
            <tr>
              <td>Price</td>
              <td colSpan="3">
              {sum}
              </td>
            </tr>
          </tbody>
        </Table>
        <Button onClick={this.onClick}>Check Out</Button>
        <Dialog ref={(component) => { this.dialog = component }} />


      </div>
    );
  }
}
