import React, { Component } from 'react'
import { Form, Button, FormControl} from 'react-bootstrap'
import { login }  from '../functionAuth/functionAuth'
const jwtDecode = require('jwt-decode')

export default class Login extends Component {
  state ={

    }
    onChangHandler=(e)=>{
        this.setState({
            [e.target.name] : e.target.value
        })
    }
    onSubmitHandelr = async (e)=>{
        e.preventDefault()
        let logSuccess = await login(this.state)
        console.log(logSuccess)
        if(logSuccess ){
            this.props.history.push('/Home')
        }
      }
    render() {
      console.log(this.state)

        return (
            <div>
              <h1>Login Page</h1>
                <Form >
  <Form.Group controlId="formGroupEmail" >
    <Form.Label>Email address</Form.Label>
    <Form.Control type="email" placeholder="Enter email" name="email"    onChange ={this.onChangHandler}/>

  </Form.Group>
  <Form.Group controlId="formGroupPassword">
    <Form.Label>Password</Form.Label>
    <Form.Control type="password" placeholder="Password" name="password"    onChange ={this.onChangHandler}/>

  </Form.Group>
</Form>
<Button variant="primary" type="submit" onSubmit={this.onSubmitHandelr}>
    Submit
  </Button>
            </div>
        )
    }

}
