import React, { Component } from "react";
import { Table, Button } from "react-bootstrap";
import {Order} from "../functionAuth/functionAuth"
export default class Cart extends Component {

  componentWillMount() {
   if ( !localStorage.product_cart   ){
      this.props.history.push('/Home')

    }
      var cart = JSON.parse(localStorage.getItem("product_cart"));
      let temp = this.state.cart;

      // temp.push(this.state.cart)
      temp.push(cart);
      this.setState({ cart: temp });
    }


    onSubmitHandelr = e => {
      e.preventDefault();
      Order(this.state.Cart);
      this.props.history.push("/Checkout");
    };

  render() {

    let elm =''
    let sum=0
  var cart = JSON.parse(localStorage.getItem("product_cart"));
    let group = cart.reduce((r, a) => {
      r[a.id] = [...(r[a.id] || []), a];
      return r;
    }, {});
    let cartelm = [];
    let j = 0;
    let total = [];
    for (let i in group) {
      cartelm.push(
        group[i].map(elm => {
          let price = elm.price * group[i].length;
          total.push(price);
          if (group[i].indexOf(elm) === 0) {
            j++;
            return (
              <tr key={elm.item_name}>
                <td>{j}</td>
                <td>{elm.name}</td>
                <td>{group[i].length}</td>
                <td>{elm.item_price} $</td>
              </tr>
            );
          }
        })
      );
    }
    // console.log(total)
    const reducer = (accumulator, currentValue) => accumulator + currentValue;
    sum = total.reduce(reducer);
    elm = cartelm.map(elm => {
      return elm;
    });


    return (
      <div>
        <Table striped bordered hover>
          <thead>
            <tr>
              <th>#</th>
              <th>Item</th>
              <th>Quantity</th>
              <th>Price</th>
            </tr>
          </thead>
          <tbody>
            {elm}
            <tr>
              <td>Total </td>
              <td colSpan="3">
              {sum} $
              </td>
            </tr>
          </tbody>
        </Table>
        <Button onClick={this.onSubmitHandelr}>Check Out</Button>


      </div>
    );
  }
}
